@use "../abstracts/index" as *;

body {
  background: teal;
}

/* input on white */
.input {
  &--flex {
    display: flex;
    flex-direction: column;
    width: 100%;
    gap: 5px;
  }
  &__label {
    font-size: 1.6rem;
    color: $black;

    &--asterisk {
      color: red;
    }
  }
  &__field {
    font-size: 1.4rem;
    height: 3rem;
    width: 100%;
    background: $white;
    padding: 0 1rem;
    border: solid 1px $input_border;
    border-radius: 5px;
    align-items: center;
  }
}

/* input on blue */

.input {
  &__label--on-blue {
    color: $white;
  }
  &__field--on-blue {
    border: none;
    background: $white;
  }
}

