@use "../abstracts/index" as *;
@use "../base/index" as *;

.header {
  width: 100%;
  height: 75px;
  @include main-gradient;

  &--space-between {
    height: 100%;
    margin: 0 3.2rem auto;
    display: flex;
    align-items: center;
    justify-content: space-between;
  }

  &__left-container {
    height: 100%;
    display: flex;
    align-items: center;
  }

  &__logo {
    width: 20rem;
    height: auto;
    margin-right: 2rem;
  }

  &__title {
    font-size: 3rem;
    font-weight: 400;
    color: $white;
    padding-left: 2rem;
    border-left: solid 2px $white;
  }

  /* 
      Tranform on the menu used to offset the 5px padding given on the menu item
  */
  &__menu {
    &-list {
      display: flex;
      gap: 3rem;
    }

    &-item {
      display: inline-block;
      padding: 1rem;
      font-size: 1.8rem;
      color: $white;
      border-bottom: solid 1px transparent;
    }
    &-item:hover {
      border-bottom: solid 1px $white;
    }
  }
}

